    function subscribe() {
        // ...
    }

    // 触发了一个 action，因此我们调用 reducer, 得到的新的 state，并且执行所有添加到 store 中的监听函数
    function dispatch() {
        // ...
    }

    // ...

    // dispatch 一个用于初始化的 action, 相当于调用一次 reducer
    // 然后将 reducer 中的 子reducer 的初始值也获取到
    // 详见下面 reducer 的实现

    return {
        dispatch,
        subscribe,
        getState,
        // 下面两个是主要面向库开发者的方法
        // replaceReducer,
        // observable
    }
}
getState()
// 外部只能通过 getState() 获取 state
function getState() {
    return currentState
}
subscribe()
function subscribe(listener) {
    // 添加到监听函数数组，
    // 注意：我们添加到了下一次 dispatch 时才会生效的数组
    nextListeners.push(listener);

    // 设置一个标志，标志该监听器已经订阅了
    let isSubscribe = true;
    // 返回取消订阅的函数，即从数组中删除该监听函数
    return function unsubscribe() {
        if(!isSubscribe) {
            // 如果已经订阅了，则直接返回
            return
        }
        isSubscribe = false;
        // 从下一轮的监听函数数组(用于下一次 dispatch)中删除这个监听器
        const index = nextListeners.indexOf(listener);
        nextListeners.splice(index, 1);
    }
}
